section_xxxv.pseudocode_block:
struct AdelicWeight {
    mpz_t archimedean_part; // Standard Float representation
    map<int, p_adic_val> local_parts; // p-adic valuations for primes 2, 3, 5, ...
};

void update_weight_adelic(AdelicWeight& w, AxiomaticGradient g) {
    // Update the Archimedean part using A-ADAM (NBQ_OCT)
    w.archimedean_part = compute_archimedean_step(w.archimedean_part, g);

    // Update local p-adic components to ensure non-Archimedean stability
    for (auto const& [p, val] : w.local_parts) {
        w.local_parts[p] = p_adic_quantize(val - g.p_valuation(p));
    }

    // Ensure the Adelic condition: |w|_p <= 1 for almost all p
    enforce_adelic_regularization(w);
}

